    final database =
        openDatabase(join(await getDatabasesPath(), 'novo.db'), version: 1);
    final Database db = await database;

    int result = await db.update(entity.tableName, entity.toMap(),
        where: 'id = ?', whereArgs: [id]);
    print(result);
    return result;
  }

